import React, { useContext, useEffect } from 'react';
import { ExerciseContext } from '../context/ExerciseState';
import Exercise from './exercise.component'

// const Exercise = props =>(
//   <tr>
//     <td>{props.exercise.username}</td>
//     <td>{props.exercise.description}</td>
//     <td>{props.exercise.duration}</td>
//     <td>{props.exercise.date.substring(0,10)}</td>
//     <td>
//       <Link to={"/edit/"+props.exercise._id}>edit</Link> | <a href="#" onClick={() => {props.deleteExercise(props.exercise._id)}}>delete</a>
//     </td>
//   </tr>
// )


const ExercisesList = () => {

  const { exercises, getExercises, deleteExercise } = useContext(ExerciseContext);

  useEffect(() => {
    getExercises();
  }, []);





  // exerciseList() {
  //   return this.state.exercises.map(currentexercise =>{
  //     // return <Exercise exercise={currentexercise} deleteExercise={this.deleteExercise} key={currentexercise._id}/>;
  //     console.log("whatever data is in the state: ", this.state.exercises)

  //   })
  // }

  return (
    <div>
      <h3>Logged Exercises</h3>
      <table className="table">
        <thead className="thead-light">
          <tr>
            <th>Username</th>
            <th>Description</th>
            <th>Duration</th>
            <th>Date</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {exercises.map(exercise => (<Exercise key={exercise._id} exercise={exercise} />))}
        </tbody>
      </table>
    </div>
  )
}

export default ExercisesList;
